import { useState, useEffect } from "react";

const useTitleCycle = () => {
    const titles = [
        "Profile",
        "UI Designer",
        "Game Developer",
        "Game Designer",
        "Frontend Developer",
        "Backend Developer",
    ];

    const [currentTitleIndex, setCurrentTitleIndex] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            // 인덱스를 다음 제목으로 이동, 마지막이면 다시 처음으로
            setCurrentTitleIndex((prevIndex) => (prevIndex + 1) % titles.length);
        }, 2000); // 2초마다 변경

        return () => clearInterval(interval); // 컴포넌트가 언마운트될 때 인터벌 정리
    }, []);

    return titles[currentTitleIndex]; // 현재 표시할 제목 반환
};

export default useTitleCycle;
