import React from "react";
import useTypingEffect from "./js/useTypingEffect";
import "./css/Profile.css"; // 스타일 파일 임포트

const Profile = () => {
    const titles = [
        "Backend Developer",
        "Frontend Developer",
        "Game Designer",
        "Game Developer",
        "UI Designer",
    ];

    // 타이핑 효과 가져오기
    const { displayText, isTextChanging } = useTypingEffect(titles, 100, 1500);

    return (
        <section className="profile-section">
            {/* 왼쪽 프로필 이미지 */}
            <div className="profile-image">
                <img src="./../../img/136299904 (1).jpeg" alt="Kim Tae Woo" />
            </div>

            {/* 오른쪽 프로필 내용 */}
            <div className="profile-content">
                <div className="typing-effect-container">
                    {/* 텍스트 변경 시 애니메이션 적용 */}
                    <h2
                        className={`profile-title typing-effect ${isTextChanging ? 'delete' : ''}`}
                    >
                        {displayText}
                    </h2>
                </div>
                <p className="profile-description">
                    안녕하세요! 저는 게임 개발자 <strong>Kim Tae Woo</strong>입니다.<br />
                    혁신적인 게임 경험을 만들기 위해 항상 노력하고 있습니다.
                </p>
            </div>
        </section>
    );
};

export default Profile;
