import { useEffect } from "react";

const useSmoothScroll = () => {
    useEffect(() => {
        let isScrolling = false;

        const scrollToSection = (direction) => {
            if (isScrolling) return; // 이미 스크롤 중일 때 이벤트 무시
            isScrolling = true;

            // 현재 스크롤 위치 가져오기
            const sections = document.querySelectorAll("section");
            const currentScroll = window.scrollY;

            // 섹션 위치를 배열로 저장
            const positions = Array.from(sections).map((section) => section.offsetTop);

            // 현재 스크롤 위치에 가장 가까운 섹션 찾기
            let targetIndex = positions.findIndex((pos) => pos > currentScroll);
            if (direction < 0) targetIndex--; // 위로 스크롤 시 이전 섹션으로 이동

            targetIndex = Math.max(0, Math.min(targetIndex, positions.length - 1)); // 인덱스 범위 제한
            const targetPosition = positions[targetIndex]; // 이동할 목표 섹션 위치

            const start = currentScroll;
            const duration = 1000; // 스크롤 지속 시간 (1초)
            let startTime = null;

            // 스무스 스크롤 애니메이션
            const animateScroll = (time) => {
                if (!startTime) startTime = time;
                const progress = Math.min((time - startTime) / duration, 1);
                const easeOutQuad = progress * (2 - progress);

                window.scrollTo(0, start + (targetPosition - start) * easeOutQuad);

                if (progress < 1) {
                    requestAnimationFrame(animateScroll);
                } else {
                    isScrolling = false;
                }
            };

            requestAnimationFrame(animateScroll);
        };

        const handleWheel = (event) => {
            const direction = event.deltaY > 0 ? 1 : -1; // 스크롤 방향 감지
            scrollToSection(direction);
        };

        window.addEventListener("wheel", handleWheel);

        return () => {
            window.removeEventListener("wheel", handleWheel);
        };
    }, []);
};

export default useSmoothScroll;
