import React, { useState } from "react";
import "./css/Project.css";
import profileImage from "../../img/KakaoTalk_Photo_2024-12-16-20-31-10 018.jpeg";

function Section({ align, imageSrc, title, subtitle }) {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div className={`section ${align}`}>
      <div
        className="section-content"
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        <div className="section-image">
          <img src={profileImage} alt="Kim Tae Woo" />
          <div className={`overlay ${isHovered ? "active" : ""}`}>
            <div className="split top"></div>
            <div className="split bottom"></div>
            <div className="hover-text">View Project →</div>
          </div>
        </div>
        <div className="section-text">
          <h2>{title}</h2>
          <p>{subtitle}</p>
        </div>
      </div>
    </div>
  );
}

export default function Project() {
  return (
    <div>
      {/* 오른쪽 정렬 섹션 */}
      <Section
        align="right"
        imageSrc="image.png"
        title="CYLNDR"
        subtitle="인터랙티브 반응형 웹"
      />

      {/* 왼쪽 정렬 섹션 */}
      <Section
        align="left"
        imageSrc="image.png"
        title="ANOTHER PROJECT"
        subtitle="크리에이티브 웹 디자인"
      />
    </div>
  );
}
