import { useState, useEffect } from "react";

const useTitleCycle = () => {
    const titles = [
        "Profile",
        "UI Designer",
        "Game Developer",
        "Game Designer",
        "Frontend Developer",
        "Backend Developer",
    ];

    const [currentTitleIndex, setCurrentTitleIndex] = useState(0); // 현재 제목 인덱스
    const [displayedTitle, setDisplayedTitle] = useState(""); // 타이핑된 텍스트

    // 타이틀 순환: 3초마다 다음 타이틀로 변경
    useEffect(() => {
        const titleChangeInterval = setInterval(() => {
            setCurrentTitleIndex((prevIndex) => (prevIndex + 1) % titles.length);
        }, 3000);

        return () => clearInterval(titleChangeInterval); // 클린업
    }, []);

    // 타이핑 효과
    useEffect(() => {
        const currentTitle = titles[currentTitleIndex]; // 현재 타이틀
        let index = 0;

        setDisplayedTitle(""); // 새 타이틀 시작 시 초기화

        const typingInterval = setInterval(() => {
            setDisplayedTitle((prev) => prev + currentTitle[index]); // 한 글자씩 추가
            index += 1;

            if (index === currentTitle.length) {
                clearInterval(typingInterval); // 타이핑 완료 시 인터벌 종료
            }
        }, 100); // 100ms마다 타이핑

        return () => clearInterval(typingInterval); // 타이틀 변경 시 클린업
    }, [currentTitleIndex]); // 타이틀 인덱스 변경 시 실행

    return displayedTitle; // 타이핑된 타이틀 반환
};

export default useTitleCycle;
