import { useState, useEffect } from "react";

const useTypingEffect = (textArray, typingSpeed = 100, pauseTime = 1500) => {
    const [displayText, setDisplayText] = useState(""); // 화면에 표시될 텍스트
    const [currentIndex, setCurrentIndex] = useState(0); // 현재 타이핑 중인 단어의 인덱스
    const [charIndex, setCharIndex] = useState(0); // 현재 단어에서 타이핑 중인 문자 인덱스
    const [isDeleting, setIsDeleting] = useState(false); // 삭제 중인지 여부

    useEffect(() => {
        // textArray가 비어있지 않은지 확인
        if (textArray.length === 0) return;

        const currentText = textArray[currentIndex];

        if (!currentText) return; // currentText가 존재하지 않으면 타이핑 중지

        let typingTimeout;

        if (isDeleting) {
            // 문자를 삭제하는 로직
            typingTimeout = setTimeout(() => {
                setDisplayText((prev) => prev.slice(0, prev.length - 1));
                setCharIndex((prev) => prev - 1);
            }, typingSpeed / 2); // 삭제 속도는 타이핑 속도의 절반
        } else {
            // 문자를 추가하는 로직
            typingTimeout = setTimeout(() => {
                setDisplayText((prev) => prev + currentText[charIndex]);
                setCharIndex((prev) => prev + 1);
            }, typingSpeed);
        }

        // 타이핑이 끝나면 삭제 모드로 전환
        if (!isDeleting && charIndex === currentText.length) {
            setTimeout(() => setIsDeleting(true), pauseTime);
        }

        // 삭제가 완료되면 다음 단어로 전환
        if (isDeleting && displayText === "") {
            setIsDeleting(false);
            setCharIndex(0);
            setCurrentIndex((prev) => (prev + 1) % textArray.length); // 다음 단어로 인덱스 변경
        }

        return () => clearTimeout(typingTimeout);
    }, [charIndex, currentIndex, displayText, isDeleting, textArray, typingSpeed, pauseTime]);

    return displayText; // 최종 화면에 표시될 타이핑된 텍스트
};

export default useTypingEffect;
