import React, { useRef } from "react";
import Sketch from "react-p5";

const ParticleSystemSketch = () => {
  let ps = []; // ParticleSystem 배열
  let offset, origin, ptime = 0;
  let bg = 0;

  // 색상 팔레트
  const colors = ["#FFE300", "#FF7494", "#00E6FF", "#89FF47"];

  // Particle 클래스
  class Particle {
    constructor(pos, ppos) {
      this.pos = pos.copy();
      this.col = random(colors);
      this.size = random(5, 30);
      this.fill = random([0, 1]);
      this.velocity = createVector(0, 0);
      this.acceleration = p5.Vector.sub(ppos, pos);
      this.acceleration.mult(0.02);
      this.offset = createVector(random(0, 1000), random(0, 1000));
    }

    update() {
      this.velocity.add(this.acceleration);
      this.pos.add(this.velocity);
      this.pos.add(createVector(10 * (noise(this.offset.x) - 0.5), 10 * (noise(this.offset.y) - 0.5)));
      this.offset.x += 0.01;
      this.offset.y += 0.01;
      this.size *= 0.95;
      this.acceleration.mult(0);
      if (this.velocity.mag() > 1) this.velocity.mult(0.95);
    }

    isDead() {
      return this.size < 1;
    }

    render(p5) {
      if (this.fill === 1) {
        p5.noStroke();
        p5.fill(this.col);
      } else {
        p5.noFill();
        p5.stroke(this.col);
      }
      p5.ellipse(this.pos.x, this.pos.y, this.size, this.size);
    }
  }

  // ParticleSystem 클래스
  class ParticleSystem {
    constructor() {
      this.particles = [];
    }

    addParticle(pos, ppos) {
      this.particles.push(new Particle(pos, ppos));
    }

    run(p5) {
      for (let i = this.particles.length - 1; i >= 0; i--) {
        const p = this.particles[i];
        p.update();
        p.render(p5);
        if (p.isDead()) this.particles.splice(i, 1);
      }
    }
  }

  const setup = (p5, canvasParentRef) => {
    p5.createCanvas(p5.windowWidth, p5.windowHeight).parent(canvasParentRef);
    ps = new ParticleSystem();
    offset = p5.createVector(p5.random(1000), p5.random(1000));
    origin = p5.createVector(p5.width / 2, p5.height / 2);
  };

  const draw = (p5) => {
    if (bg % 2 === 0) p5.background(87, 6, 140);
    else p5.background(0, 0, 0, 20);

    if (p5.mouseX === p5.pmouseX && p5.mouseY === p5.pmouseY && p5.millis() - ptime > 800) {
      const step = p5.createVector(p5.noise(offset.x) - 0.5, p5.noise(offset.y) - 0.5);
      step.mult(10);
      let pos = p5.Vector.add(origin, step);
      pos.x = (pos.x + p5.width) % p5.width;
      pos.y = (pos.y + p5.height) % p5.height;

      ps.addParticle(pos, origin);
      origin = pos;
      offset.x += 0.01;
      offset.y += 0.01;
    }

    ps.run(p5);
  };

  const windowResized = (p5) => {
    p5.resizeCanvas(p5.windowWidth, p5.windowHeight);
  };

  const mouseClicked = () => {
    bg++;
  };

  const mouseMoved = (p5) => {
    const pos = p5.createVector(p5.mouseX, p5.mouseY);
    const ppos = p5.createVector(p5.pmouseX, p5.pmouseY);
    ps.addParticle(pos, ppos);
    ptime = p5.millis();
    origin = p5.createVector(p5.width / 2, p5.height / 2); // 리셋
  };

  return (
    <Sketch
      setup={setup}
      draw={draw}
      windowResized={windowResized}
      mouseClicked={mouseClicked}
      mouseMoved={mouseMoved}
    />
  );
};

export default ParticleSystemSketch;
