import React, { useEffect, useState } from "react";
import useTitleCycle from "./useTitleCycle"; // 커스텀 훅 임포트
import "./css/Profile.css"; // 스타일 파일 임포트

const Profile = () => {
    const title = useTitleCycle(); // 커스텀 훅 사용
    const [displayedTitle, setDisplayedTitle] = useState(""); // 화면에 표시될 제목 상태

    useEffect(() => {
        // 타이핑 효과가 끝난 후 글자를 전환
        let index = 0;
        const currentTitle = title;
        const interval = setInterval(() => {
            setDisplayedTitle((prev) => prev + currentTitle[index]);
            index += 1;
            if (index === currentTitle.length) {
                clearInterval(interval); // 타이핑이 끝나면 인터벌 종료
            }
        }, 100); // 100ms마다 하나씩 타이핑

        // 컴포넌트가 업데이트될 때마다 타이핑 시작
        return () => setDisplayedTitle(""); // 새로 타이핑을 시작할 때 초기화
    }, [title]);

    return (
        <section className="profile-section">
            {/* 왼쪽 프로필 이미지 */}
            <div className="profile-image">
                <img src="your-photo.jpg" alt="Kim Tae Woo" />
            </div>

            {/* 오른쪽 프로필 내용 */}
            <div className="profile-content">
                <h2 className="profile-title">{displayedTitle}</h2>
                <p className="profile-description">
                    안녕하세요! 저는 게임 개발자 <strong>Kim Tae Woo</strong>입니다.<br />
                    혁신적인 게임 경험을 만들기 위해 항상 노력하고 있습니다.
                </p>
            </div>
        </section>
    );
};

export default Profile;
