import React, { useRef, useEffect } from "react";

const ParticleSystemSketch = () => {
  const canvasRef = useRef(null);
  const particles = useRef([]);
  const MAX_PARTICLES = 500;
  let animationFrameId;

  class Particle {
    constructor(x, y) {
      this.x = x;
      this.y = y;
      this.size = Math.random() * 25 + 5;
      this.color = `rgba(${Math.floor(Math.random() * 255)}, 
                         ${Math.floor(Math.random() * 255)}, 
                         ${Math.floor(Math.random() * 255)}, 
                         1)`;
      this.velocityX = (Math.random() - 0.5) * 4;
      this.velocityY = (Math.random() - 0.5) * 4;
      this.life = Math.random() * 80 + 40;
    }

    update() {
      this.x += this.velocityX;
      this.y += this.velocityY;
      this.size *= 0.95;
      this.life -= 2;
    }

    render(ctx) {
      if (this.life > 0) {
        ctx.beginPath();
        ctx.fillStyle = this.color;
        ctx.globalAlpha = this.life / 100;
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.fill();
        ctx.closePath();
      }
    }

    isDead() {
      return this.life <= 0 || this.size < 1;
    }
  }

  const handleMouseMove = (e) => {
    const x = e.clientX;
    const y = e.clientY;

    for (let i = 0; i < 3; i++) {
      if (particles.current.length >= MAX_PARTICLES) {
        particles.current.shift();
      }
      particles.current.push(new Particle(x, y));
    }
  };

  const animate = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    particles.current.forEach((particle, index) => {
      particle.update();
      particle.render(ctx);
      if (particle.isDead()) {
        particles.current.splice(index, 1);
      }
    });

    animationFrameId = requestAnimationFrame(animate);
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    animate();
    window.addEventListener("mousemove", handleMouseMove);

    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };
    window.addEventListener("resize", handleResize);

    return () => {
      cancelAnimationFrame(animationFrameId);
      window.removeEventListener("mousemove", handleMouseMove);
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return <canvas ref={canvasRef} style={{ display: "block", position: "absolute", top: 0, left: 0, zIndex: -1 }} />;
};

export default ParticleSystemSketch;
