import React, { useState } from "react";
import "./css/Skills.css";
import reactLogo from "../../img/KakaoTalk_Photo_2024-12-16-20-31-10 018.jpeg";
import vueLogo from "../../img/KakaoTalk_Photo_2024-12-16-20-31-10 018.jpeg";
import nodeLogo from "../../img/KakaoTalk_Photo_2024-12-16-20-31-10 018.jpeg";
import jsLogo from "../../img/KakaoTalk_Photo_2024-12-16-20-31-10 018.jpeg";
import tsLogo from "../../img/KakaoTalk_Photo_2024-12-16-20-31-10 018.jpeg";

const Skills = () => {
  const [hoveredSkill, setHoveredSkill] = useState("");

  // 스킬 리스트와 이미지 경로 매핑
  const skills = [
    { name: "React", logo: reactLogo },
    { name: "Vue", logo: vueLogo },
    { name: "Node.js", logo: nodeLogo },
    { name: "JavaScript", logo: jsLogo },
    { name: "TypeScript", logo: tsLogo },
  ];

  return (
    <section className="skills-section">
      <h2 className="skills-title">스택</h2>
      <div className="skills-container">
        {skills.map((skill, index) => (
          <div
            key={index}
            className="skill-card"
            onMouseEnter={() => setHoveredSkill(skill.name)}
            onMouseLeave={() => setHoveredSkill("")}
          >
            <img src={skill.logo} alt={skill.name} className="skill-logo" />
            <div className={`skill-name ${hoveredSkill === skill.name ? "show" : ""}`}>
              {skill.name}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Skills;
